mapper.select:
SELECT no, explorer_no explorerNo, title, content FROM posts WHERE no in ((select no from posts where
category_type IN('N','F','T','P')
category_type = #{categoryType}
AND no < #{no}
order by no DESC limit 1), (select no from posts where
category_type IN('N','F','T','P')
category_type = #{categoryType}
AND no > #{no} order by no limit 1))

SELECT TB3.*
from( SELECT TB2.*, row_number() over(ORDER BY
TB2.counts DESC
TB2.no DESC
) as r
FROM(
SELECT TB1.* , SUM(TB1.countLikes+TB1.countComments+TB1.countViews) as counts FROM( SELECT p.no no, p.explorer_no explorerNo, p.title title, p.content content, p.category_type categoryType, p.regdate regdate, e.email email, e.nickname nickname, e.profile profile, e.score score,
(SELECT COUNT(l.no) FROM likes l WHERE p.no = l.content_no AND l.content_type='P') as countLikes, (SELECT count(c.no) FROM comments c WHERE p.no = c.post_no ) as countComments, (SELECT count(*) FROM views v WHERE p.no = v.post_no ) as countViews
FROM posts p left join explorers e on p.explorer_no = e.no
WHERE
p.category_type IN('N','F','T','P')
p.category_type = #{categoryType}
and
p.title Like #{text}
e.nickname Like #{text}
(p.title Like #{text} OR p.content Like #{text})
) as TB1
group by TB1.no
) as TB2 )as TB3
WHERE TB3.r BETWEEN #{start} AND #{end}

SELECT count(*) FROM posts p left join explorers e on p.explorer_no = e.no WHERE
p.category_type IN('N','F','T','P')
p.category_type = #{categoryType}
and
(p.title Like #{text} OR p.content Like #{text})
p.title Like #{text}
e.nickname Like #{text}
